// Import Superagent
const superagent = require('superagent');

class Dog {
    constructor() {
        this.api = 'https://random.dog/woof.json';
    }

    getDog() {
        return new Promise((resolve, reject) => {superagent.get(this.api, (err, res) => {
                if (err) reject(err);
                resolve(res.body.url);
            })
        })
    }
}

module.exports = Dog;